mapper.insert:
INSERT INTO NOTICE(
    TITLE,
    CONTENT,
    WRITER_ID
) VALUES(
    #{title},
    #{content},
    (
        SELECT M.ID
            FROM MEMBER M
            WHERE M.NICKNAME = #{nickname}
    )
)

mapper.update:
UPDATE NOTICE
SET
    TITLE = #{title},
    CONTENT = #{content},
    MODDATE = #{moddate}
WHERE ID = #{id}
